document.addEventListener("DOMContentLoaded", () => {
  const observer = new IntersectionObserver((entries) => {
    entries.forEach((entry) => {
      const elements = entry.target.querySelectorAll(".blinking-decor-block");
      if (entry.isIntersecting) {
        elements.forEach((el) => el.classList.remove("inactive"));
      } else {
        elements.forEach((el) => el.classList.add("inactive"));
      }
    });
  });

  const target = document.querySelector(".blinking-decor-block-wrap");
  if (target) observer.observe(target);
});

document.addEventListener("DOMContentLoaded", () => {
  const sections = document.querySelectorAll("[data-text-color]");
  const responsiveTexts = document.querySelectorAll("[data-responsive-text='true']");
  const lineMaskLinks = document.querySelectorAll("[line-mask-link]");
  const menu = document.querySelector("[data-menu]");

  const defaultColor = "#070707";
  const defaultBgColor = "transparent";

  responsiveTexts.forEach((textElement) => {
    textElement.style.color = defaultColor;
    textElement.style.backgroundColor = defaultBgColor;
    textElement.style.setProperty("--shadow-color", defaultColor);
  });

  let activeColor = defaultColor;
  let activeBgColor = defaultBgColor;

  function getActiveSection() {
    let foundSection = null;
    sections.forEach((section) => {
      const rect = section.getBoundingClientRect();
      if (rect.top < window.innerHeight * 0.5 && rect.bottom > window.innerHeight * 0.5) {
        foundSection = section;
      }
    });
    return foundSection;
  }

  function updateTextColor() {
    const activeSection = getActiveSection();
    if (activeSection) {
      activeColor = activeSection.getAttribute("data-text-color") || defaultColor;
      activeBgColor = activeSection.getAttribute("data-bg-color") || defaultBgColor;
    } else {
      activeColor = defaultColor;
      activeBgColor = defaultBgColor;
    }

    responsiveTexts.forEach((textElement) => {
      textElement.style.color = activeColor;
      textElement.style.backgroundColor = activeBgColor;
      textElement.style.setProperty("--shadow-color", activeColor);
    });
  }

  const observer = new IntersectionObserver(
    (entries) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          activeColor = entry.target.getAttribute("data-text-color") || defaultColor;
          activeBgColor = entry.target.getAttribute("data-bg-color") || defaultBgColor;
        }
      });

      updateTextColor();
    },
    { threshold: 0.5 }
  );

  sections.forEach((section) => observer.observe(section));

  if (menu) {
    menu.addEventListener("transitionend", updateTextColor);
    menu.addEventListener("click", updateTextColor);
  }

  lineMaskLinks.forEach((link) => {
    const lines = link.querySelectorAll("[line-mask-text]");

    link.addEventListener("mouseenter", function () {
      gsap.to(lines, {
        yPercent: -100,
        duration: 0.2,
        ease: "ease",
        overwrite: true,
      });
    });

    link.addEventListener("mouseleave", function () {
      gsap.to(lines, {
        yPercent: 0,
        duration: 0.2,
        ease: "ease",
      });
    });
  });

  setTimeout(updateTextColor, 100);

  let scrollTimeout;
  window.addEventListener("scroll", () => {
    clearTimeout(scrollTimeout);
    scrollTimeout = setTimeout(updateTextColor, 80);
  });
});

document.addEventListener("DOMContentLoaded", () => {
  const triggerElements = document.querySelectorAll('[data-hover-slide-trigger]');

  triggerElements.forEach((trigger) => {
    const animatedElements = trigger.querySelectorAll('[data-animation="slideup"]');

    animatedElements.forEach((el) => {
      const mask = document.createElement("div");
      mask.classList.add("dynamic-mask");

      const span = document.createElement("span");
      span.innerHTML = el.innerHTML.trim();

      mask.appendChild(span);
      el.innerHTML = "";
      el.appendChild(mask);
    });

    trigger.addEventListener("mouseenter", () => {
      const masks = trigger.querySelectorAll(".dynamic-mask span");
      masks.forEach((span) => {
        const parent = span.closest('[data-animation="slideup"]');
        const delay = parseFloat(parent.getAttribute("slide-delay") || "0");
        const speed = parseFloat(parent.getAttribute("slide-speed") || "0.5");

        span.style.animation = "none";
        void span.offsetWidth;
        span.style.animation = `slideup ${speed}s ${delay}s cubic-bezier(0.785, 0.135, 0.15, 0.86) forwards`;
      });
    });

    trigger.addEventListener("mouseleave", () => {
      const masks = trigger.querySelectorAll(".dynamic-mask span");
      masks.forEach((span) => {
        span.style.animation = "none";
      });
    });
  });
});

document.addEventListener("DOMContentLoaded", () => {
  document.body.addEventListener("contextmenu", (e) => {
    if (e.target.tagName === "IMG" || e.target.tagName === "SVG") {
      e.preventDefault();
    }
  });

  document.querySelectorAll("img, a, svg").forEach(el => {
    el.setAttribute("draggable", "false");
  });
});

// Проверяем ширину окна
if (window.innerWidth > 768) {
  // Находим элементы по ID
  const xCoord = document.getElementById("x-coordinate");
  const yCoord = document.getElementById("y-coordinate");

  // Функция для форматирования числа
  function formatNumber(num) {
    return num.toString().padStart(4, "0"); // Добавляем ведущие нули до 4 символов
  }

  // Отслеживаем движение курсора
  document.addEventListener("mousemove", (event) => {
    // Обновляем текст координат
    xCoord.textContent = formatNumber(event.clientX % 10000); // Ограничиваем максимум 9999
    yCoord.textContent = formatNumber(event.clientY % 10000);
  });
}

document.addEventListener("DOMContentLoaded", () => {
  const chars = "ABCDEFGHKLMNOPQRSTUVWXYZ";
  let isClickable = true;
  const indicator = document.getElementById("section-indicator");
  const sections = document.querySelectorAll("[data-title]");
  let currentSection = "";

  function generateRandomChars(count, finalChar) {
    const result = [];
    for (let i = 0; i < count; i++) {
      result.push(chars.charAt(Math.floor(Math.random() * chars.length)));
    }
    result.push(finalChar);
    return result;
  }

  function scrambleAnimate(element, text, useSplitType = false, skipDelay = false) {
    const speed = parseFloat(element.getAttribute("data-speed")) || 8;
    const animationType = element.getAttribute("data-animation") || "sequential";
    const delay = skipDelay ? 0 : parseFloat(element.getAttribute("data-delay")) || 0;

    if (useSplitType) {
      if (element.splitInstance) element.splitInstance.revert();
      element.splitInstance = new SplitType(element, { types: "words, chars" });

      const charsEls = element.querySelectorAll(".char");
      const timeline = gsap.timeline({ paused: true });
      const timeline1 = gsap.timeline();

      charsEls.forEach((char) => {
        const randomChars = generateRandomChars(3, char.textContent);
        char.innerHTML = "";
        randomChars.forEach((c, i) => {
          const span = document.createElement("span");
          span.innerText = c;
          span.style.opacity = "0";
          span.style.position = i < 3 ? "absolute" : "relative";
          if (i < 3) span.style.textShadow = "none";
          if (i === 3) span.classList.add("last");
          char.appendChild(span);
        });
      });

      const items = animationType === "random"
        ? Array.from(charsEls).sort(() => Math.random() - 0.5)
        : charsEls;

      items.forEach((char, index) => {
        timeline1.to(char.children, {
          opacity: 1,
          stagger: {
            each: 0.2,
            onUpdate: function () {
              if (this.progress() > 0.6 && !this._targets[0].classList.contains("last")) {
                this._targets[0].style.opacity = "0";
              }
            }
          }
        }, `index+=${index / 2}`);
      });

      timeline.add(timeline1.timeScale(speed).delay(delay));

      const startPos = element.hasAttribute("data-start-100") ? "top 100%" : "top 85%";
      ScrollTrigger.create({
        trigger: element,
        start: startPos,
        end: "top 50%",
        onEnter: () => timeline.play(),
        onLeaveBack: () => timeline.reverse(),
      });

    } else {
      element.innerHTML = "";
      element.style.whiteSpace = "pre-wrap";

      text.split(" ").forEach((word, wordIndex, arr) => {
        const wordSpan = document.createElement("span");
        wordSpan.style.display = "inline-block";
        word.split("").forEach((char) => {
          const span = document.createElement("span");
          span.style.position = "relative";
          span.style.display = "inline-block";

          const randomChars = generateRandomChars(3, char);
          randomChars.forEach((c, i) => {
            const inner = document.createElement("span");
            inner.innerText = c;
            inner.style.opacity = "0";
            inner.style.position = i < 3 ? "absolute" : "relative";
            if (i < 3) inner.style.textShadow = "none";
            if (i === 3) inner.classList.add("final-char");
            span.appendChild(inner);
          });

          wordSpan.appendChild(span);
        });

        element.appendChild(wordSpan);
        if (wordIndex < arr.length - 1) {
          element.appendChild(document.createTextNode(" "));
        }
      });

      const spans = element.querySelectorAll("span span");
      const items = animationType === "random"
        ? [...spans].sort(() => Math.random() - 0.5)
        : spans;

      const timeline = gsap.timeline().timeScale(speed);
      items.forEach((span, i) => {
        timeline.to(span.children, {
          opacity: 1,
          stagger: {
            each: 0.1,
            onUpdate: function () {
              if (this.progress() > 0.6 && !this._targets[0].classList.contains("final-char")) {
                this._targets[0].style.opacity = "0";
              }
            }
          }
        }, i * 0.05);
      });
    }
  }

  document.querySelectorAll("[data-text]").forEach((el) => {
    scrambleAnimate(el, el.getAttribute("data-text"), true);
  });

  document.querySelectorAll("[data-collection-link]").forEach((link) => {
    link.addEventListener("click", () => {
      if (!isClickable) return;
      isClickable = false;
      setTimeout(() => isClickable = true, 2000);

      const isPrimary = link.getAttribute("data-collection-link") === "1";

      document.querySelectorAll("[data-text]").forEach((el) => {
        const original = el.getAttribute("data-original-text") || el.getAttribute("data-text");
        const newText = el.getAttribute("data-new-text");
        if (!el.hasAttribute("data-original-text")) {
          el.setAttribute("data-original-text", original);
        }
        const target = isPrimary ? original : newText;

        if (target && target !== el.getAttribute("data-text")) {
          gsap.to(el, {
            opacity: 0,
            duration: 0.3,
            onComplete: () => {
              if (el.splitInstance) {
                el.splitInstance.revert();
                el.splitInstance = null;
              }
              el.setAttribute("data-text", target);
              el.innerHTML = target;
              el.splitInstance = new SplitType(el, { types: "words, chars" });
              scrambleAnimate(el, target, true, true);
              gsap.to(el, { opacity: 1, duration: 0.3 });
            }
          });
        }
      });
    });
  });

  const updateIndicator = (newTitle) => {
    if (newTitle !== currentSection) {
      currentSection = newTitle;
      indicator.setAttribute("data-text", newTitle);
      scrambleAnimate(indicator, newTitle, false);
    }
  };

  const setInitialIndicator = () => {
    const rect = sections[0].getBoundingClientRect();
    if (rect.top <= window.innerHeight * 0.6 && rect.bottom >= window.innerHeight * 0.6) {
      const title = sections[0].dataset.title;
      currentSection = title;
      indicator.setAttribute("data-text", title);
      scrambleAnimate(indicator, title, false);
    }
  };

  window.addEventListener("scroll", () => {
    let newSection = "";
    sections.forEach((section) => {
      const rect = section.getBoundingClientRect();
      if (rect.top <= window.innerHeight * 0.6 && rect.bottom >= window.innerHeight * 0.6) {
        newSection = section.dataset.title;
      }
    });
    if (newSection) updateIndicator(newSection);
  });

  setInitialIndicator();

  setTimeout(() => {
    scrambleAnimate(indicator, indicator.getAttribute("data-text"), false);
  }, 100);
});

