const backgroundMusicURL = "https://www.dropbox.com/scl/fi/vqcf8ce67qlhvy3mhsxcq/bg-music-dich.mp3?rlkey=9074752jvy6u7gzpnrxo5qxig&raw=1";
const hoverSoundURL = "https://www.dropbox.com/scl/fi/llh0lafsdd02k4a89lg6m/hover-effect-dich.mp3?rlkey=xf5rn79uo9klimtnq1otxenvj&raw=1";
const clickSoundURL = "https://www.dropbox.com/scl/fi/47gqhe6ilmfughe60u5m9/click-1-effect-dich.mp3?rlkey=czmv0eztgfi33zzbtsm3b6z7h&raw=1";

const backgroundMusic = new Audio(backgroundMusicURL);
const hoverSound = new Audio(hoverSoundURL);
const clickSound = new Audio(clickSoundURL);
backgroundMusic.loop = true;

let isMusicPlaying = false;
let lastUIState = null;
let fadeInProcess = null;
let fadeOutProcess = null;

const backgroundMusicVolume = 0.5;
const hoverSoundVolume = 0.75;
const clickSoundVolume = 0.75;

function updateSoundUI() {
  if (lastUIState === isMusicPlaying) return;
  lastUIState = isMusicPlaying;
  document.querySelectorAll("[sound-panel]").forEach(panel => panel.hidden = isMusicPlaying);
  document.querySelectorAll("[sound-panel-2]").forEach(panel => panel.hidden = !isMusicPlaying);
}

function stopFadeProcesses() {
  if (fadeInProcess) clearInterval(fadeInProcess);
  if (fadeOutProcess) clearInterval(fadeOutProcess);
  fadeInProcess = null;
  fadeOutProcess = null;
}

function toggleBackgroundMusicWithFade(play = true) {
  const duration = 250;
  const steps = 25;
  const interval = duration / steps;
  const stepSize = 1 / steps;

  stopFadeProcesses();

  if (play) {
    backgroundMusic.volume = 0;
    hoverSound.volume = 0;
    clickSound.volume = 0;

    backgroundMusic.play();
    isMusicPlaying = true;

    let currentStep = 0;
    fadeInProcess = setInterval(() => {
      currentStep++;
      const progress = Math.min(currentStep * stepSize, 1);
      backgroundMusic.volume = progress * backgroundMusicVolume;
      hoverSound.volume = progress * hoverSoundVolume;
      clickSound.volume = progress * clickSoundVolume;
      if (progress >= 1) {
        clearInterval(fadeInProcess);
        fadeInProcess = null;
      }
    }, interval);
  } else {
    let currentVolume = backgroundMusic.volume;
    const stepAmount = currentVolume / steps;

    fadeOutProcess = setInterval(() => {
      currentVolume -= stepAmount;
      const progress = Math.max(currentVolume, 0);
      backgroundMusic.volume = progress;
      hoverSound.volume = progress;
      clickSound.volume = progress;
      if (progress <= 0) {
        clearInterval(fadeOutProcess);
        fadeOutProcess = null;
        backgroundMusic.pause();
      }
    }, interval);

    isMusicPlaying = false;
  }

  updateSoundUI();
}

function applyVolumes() {
  backgroundMusic.volume = backgroundMusicVolume;
  hoverSound.volume = hoverSoundVolume;
  clickSound.volume = clickSoundVolume;
}

document.addEventListener("click", (event) => {
  if (event.target.closest("[data-sound-on]")) {
    toggleBackgroundMusicWithFade(true);
  }

  if (event.target.closest("[data-sound-mode]")) {
    toggleBackgroundMusicWithFade(!isMusicPlaying);
  }

  if (event.target.closest("[data-sound-click]") && isMusicPlaying) {
    clickSound.currentTime = 0;
    clickSound.play();
  }
});

document.addEventListener("mouseover", (event) => {
  if (!isMusicPlaying) return;
  const target = event.target.closest("[data-sound-hover]");
  if (target && !target.hasAttribute("data-sound-playing")) {
    target.setAttribute("data-sound-playing", "true");
    hoverSound.currentTime = 0;
    hoverSound.play();
    target.addEventListener("mouseleave", () => {
      target.removeAttribute("data-sound-playing");
    }, { once: true });
  }
});

document.addEventListener("DOMContentLoaded", updateSoundUI);
applyVolumes();

(function() {
  function isSessionStorageAvailable() {
    try {
      sessionStorage.setItem("test", "test");
      sessionStorage.removeItem("test");
      return true;
    } catch (e) {
      return false;
    }
  }

  const isNewTab = !window.name;
  if (isNewTab) {
    window.name = "webflow-tab";
  }

  const preloader = document.querySelector(".preloader-enter");
  const preloaderButton = document.querySelector(".preloader_button");

  if (isSessionStorageAvailable()) {
    if (isNewTab) {
      sessionStorage.removeItem("preloaderHidden");
    }

    if (sessionStorage.getItem("preloaderHidden") === "true") {
      if (preloader) preloader.remove();
    } else {
      if (preloaderButton) {
        preloaderButton.addEventListener("click", function() {
          sessionStorage.setItem("preloaderHidden", "true");

          setTimeout(() => {
            if (preloader) preloader.remove();
