$(document).ready(function () {
  $("a").on("click", function (e) {
    if (
      $(this).prop("hostname") === window.location.host &&
      $(this).attr("href").indexOf("#") === -1 &&
      $(this).attr("target") !== "_blank"
    ) {
      e.preventDefault();
      let destination = $(this).attr("href");
      gsap.set(".load_grid", { display: "grid" });
      gsap.fromTo(
        ".load_grid-item",
        { opacity: 0, y: 10 },
        {
          opacity: 1,
          y: 0,
          duration: 0.035,
          stagger: { each: 0.0082, from: "center" },
          ease: "power2.out",
          onComplete: () => {
            window.location = destination;
          }
        }
      );
    }
  });

  window.onpageshow = function (event) {
    if (event.persisted) {
      window.location.reload();
    }
  };
});

document.addEventListener("DOMContentLoaded", () => {
  const preloader = document.querySelector(".preloader-enter");
  const preloaderBtn = document.querySelector(".preloader_button-wrap");
  const lazySections = document.querySelectorAll(".lazy-section");

  const io = new IntersectionObserver(
    (entries, observer) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const el = entry.target;
          const label = el.dataset.section || el.id || el.className;
          el.classList.add("active");

          const images = el.querySelectorAll("img[data-src]");
          images.forEach((img) => {
            if (img.dataset.src) {
              img.src = img.dataset.src;
            }
          });

          observer.unobserve(el);
        }
      });
    },
    { rootMargin: "0px 0px 200px 0px" }
  );

  const startObservingSections = () => {
    lazySections.forEach((sec) => io.observe(sec));
  };

  if (!preloader || preloader.style.display === "none") {
    startObservingSections();
    return;
  }

  const preloaderWatcher = new MutationObserver((_, obs) => {
    if (preloader.style.display === "none") {
      startObservingSections();
      obs.disconnect();
    }
  });

  preloaderWatcher.observe(preloader, {
    attributes: true,
    attributeFilter: ["style"],
  });

  if (preloaderBtn) {
    preloaderBtn.addEventListener("click", () => {
      startObservingSections();
      preloaderWatcher.disconnect();
    });
  }
});

document.addEventListener("DOMContentLoaded", function () {
  const lottieLib = window.Webflow.require('lottie').lottie;
  const lottieElements = document.querySelectorAll("[data-lottie-watch]");

  const observer = new IntersectionObserver(
    (entries) => {
      entries.forEach((entry) => {
        const el = entry.target;
        const instance = lottieLib.getRegisteredAnimations().find(a => a.wrapper === el);
        if (!instance) return;

        if (entry.isIntersecting) {
          instance.play();
        } else {
          instance.pause();
        }
      });
    },
    { threshold: 0.1 }
  );

  lottieElements.forEach((el) => observer.observe(el));
});

function isVisible(selector) {
  const el = document.querySelector(selector);
  return el && window.getComputedStyle(el).display !== 'none';
}

function updateScrollLock() {
  const shouldLockScroll =
    isVisible('.nav_menu') ||
    isVisible('.preloader-enter') ||
    isVisible('.modal');

  if (typeof lenis !== "undefined") {
    shouldLockScroll ? lenis.stop() : lenis.start();
  }
}

document.addEventListener("DOMContentLoaded", () => {
  updateScrollLock();

  const targets = ['.nav_menu', '.preloader-enter', '.modal'];

  targets.forEach(selector => {
    const el = document.querySelector(selector);
    if (!el) return;

    const observer = new MutationObserver(updateScrollLock);
    observer.observe(el, { attributes: true, attributeFilter: ['style', 'class'] });
  });
});

const originalWarn = console.warn;
console.warn = function (...args) {
  if (args[0].includes("GSAP target")) {
    return;
  }
  if (
    args[0].includes("three.js") ||
    args[0].includes("three.min.js") ||
    args[0].includes("Multiple instances of Three.js")
  ) {
    return;
  }
  originalWarn.apply(console, args);
};

