document.addEventListener("DOMContentLoaded", () => {
  const preloaderBlack = document.querySelector('.preloader-black');
  const lines = document.querySelectorAll('.preloader_line');
  const blocks = document.querySelectorAll('.is-top-1, .is-top-2, .is-top-3, .is-center-1, .is-center-2-1, .is-center-2-2, .is-center-3, .is-bottom-1, .is-bottom-2, .is-bottom-3');
  const corners = document.querySelector('.preloader_corners');
  const text = document.querySelector('.preloader_text');
  const preloaderButton = document.querySelector('[data-enter-hide]');
  const preloaderEnterButton = document.querySelector('.button-circle-preloader');
  const lottiePreloader = document.querySelector('.lottie_preloader');
  const enterBlock = document.querySelector('.preloader-enter');

  if (preloaderBlack) {
  gsap.to(preloaderBlack, {
    opacity: 0,
    duration: 0.25,
    onComplete: () => {
      preloaderBlack.style.display = "none";
    }
  });
}

  lines.forEach(line => {
    const isTop = line.classList.contains('is-top');
    const isBottom = line.classList.contains('is-bottom');
    const isLeft = line.classList.contains('is-left');
    const isRight = line.classList.contains('is-right');

    if (isTop || isBottom) {
      gsap.fromTo(line,
        { width: 0 },
        { width: '100%', duration: 1.25, delay: 1, ease: 'power2.inOut' }
      );
    } else if (isLeft || isRight) {
      gsap.fromTo(line,
        { height: 0 },
        { height: '100%', duration: 1.25, delay: 1, ease: 'power2.inOut' }
      );
    }
  });

  if (preloaderEnterButton) {
    gsap.delayedCall(5.25, () => {
      preloaderEnterButton.classList.add('is-active');
    });
  }

  preloaderButton?.addEventListener("click", () => {
    const tl = gsap.timeline();

    if (preloaderEnterButton && lottiePreloader) {
      tl.to([preloaderEnterButton, lottiePreloader], {
        scale: 0,
        opacity: 0,
        duration: 0.25,
        ease: "power2.inOut"
      }, 0);
    }

    tl.to(lines, {
      opacity: 0,
      duration: 0.5,
      ease: "power2.out"
    }, 0.25);

    blocks.forEach(block => {
      let props = {};
      if (block.classList.contains('is-top-1') || block.classList.contains('is-bottom-3')) {
        props = { height: 0 };
      } else if (block.classList.contains('is-top-2') || block.classList.contains('is-center-3') || block.classList.contains('is-center-2-2')) {
        props = { width: 0 };
      } else if (block.classList.contains('is-top-3') || block.classList.contains('is-bottom-1')) {
        props = { height: 0 };
      } else if (block.classList.contains('is-center-1') || block.classList.contains('is-bottom-2') || block.classList.contains('is-center-2-1')) {
        props = { width: 0 };
      }
      tl.to(block, { ...props, duration: 1, ease: "power2.inOut" }, 0.25);
    });

    tl.to(corners, {
      opacity: 0,
      duration: 0.1,
      ease: "power1.out"
    }, 0.25);

    tl.to(text, {
      opacity: 0,
      duration: 0.1,
      ease: "power1.out"
    }, 0.25);

    tl.to(enterBlock, {
      opacity: 0,
      duration: 0.2,
      ease: "power1.out",
      onComplete: () => {
        enterBlock.remove();
      }
    }, "+=1.5");
  });
});

(function () {
    const animateNumber = (element, target, duration, callback) => {
        let startTime;
        const initialNumber = 0;

        const cubicBezier = (t, p0, p1, p2, p3) => {
            const u = 1 - t;
            return 3 * u * u * t * p1 + 3 * u * t * t * p2 + t * t * t * p3;
        };

        const easingFunction = t => cubicBezier(t, 0, 0.645, 0.355, 1);

        const animate = time => {
            if (!startTime) startTime = time;
            const elapsedTime = time - startTime;
            const t = Math.min(elapsedTime / duration, 1);
            const easedT = easingFunction(t);
            const newValue = initialNumber + (target - initialNumber) * easedT;

            const roundedValue = Math.floor(newValue);

            if (parseInt(element.textContent, 10) !== roundedValue) {
                element.textContent = roundedValue;
            }

            if (elapsedTime < duration) {
                requestAnimationFrame(animate);
            } else {
                element.textContent = target;
                if (callback) callback();
            }
        };

        requestAnimationFrame(animate);
    };

    const onIntersection = entries => {
        entries.forEach(entry => {
            if (entry.isIntersecting) {
                const el = entry.target;
                const finalNumber = parseInt(el.getAttribute("data-final-number"), 10);
                const animDuration = 3250;

                el.textContent = "0";

                setTimeout(() => {
                    animateNumber(el, finalNumber, animDuration, () => {
                        setTimeout(() => {
                            document.querySelectorAll(".preloader_load-text-big, .preloader_load-text-small").forEach(text => {
                                text.style.transform = "translateY(100%)";
                                text.style.opacity = "0";
                            });
                        }, 250);

                    });
                }, 250);

                counterObserver.unobserve(el);
            }
        });
    };

    const counterObserver = new IntersectionObserver(onIntersection);

    document.addEventListener("DOMContentLoaded", () => {
        document.querySelectorAll('[counter-element="number"]').forEach(el => {
            el.setAttribute("data-final-number", el.textContent);
            el.textContent = "0";
            counterObserver.observe(el);
        });
    });
})();

document.addEventListener("scroll", () => {
  const scrollHeight = document.documentElement.scrollHeight - document.documentElement.clientHeight;
  const scrollTop = window.scrollY;
  const scrollPercent = Math.min((scrollTop / scrollHeight) * 100, 100);

  document.querySelectorAll(".progress-bar-wrap").forEach((progressBarWrap) => {
    const dot1 = progressBarWrap.querySelector(".progresbar_dot-1");
    const line = progressBarWrap.querySelector(".progress_line .progress_time-line");
    const dot2 = progressBarWrap.querySelector(".progresbar_dot-2");

    if (scrollPercent <= 3) {
      gsap.set(dot1, { backgroundColor: "#393736" });
    }

    if (scrollPercent > 3 && scrollPercent <= 10) {
      gsap.to(dot1, {
        backgroundColor: "#A6A19D",
        duration: 0.2,
        overwrite: "auto"
      });
    }

    if (scrollPercent > 10 && scrollPercent <= 95) {
      const heightPercent = ((scrollPercent - 10) / 85) * 100;
      gsap.set(line, { height: `${heightPercent}%` });
    }

    if (scrollPercent >= 99.9) {
      gsap.set(line, { height: "100%" });
    }

    if (scrollPercent <= 10) {
      gsap.set(line, { height: "0%" });
    }

    if (scrollPercent > 95) {
      gsap.to(dot2, {
        backgroundColor: "#A6A19D",
        duration: 0.2,
        overwrite: "auto"
      });
    } else {
      gsap.to(dot2, {
        backgroundColor: "#393736",
        duration: 0.2,
        overwrite: "auto"
      });
    }
  });
});

document.addEventListener("DOMContentLoaded", initNavbar);

function initNavbar() {
  const elements = {
    navButton: document.querySelector(".nav_button"),
    closeTriggers: document.querySelectorAll("[nav-close-trigger]"),
    hoverPairs: [
      {
        trigger: document.querySelector("[anturax-nav-hover]"),
        from: document.querySelector(".nav_content-left"),
        to: document.querySelector(".nav_content-left-2"),
      },
      {
        trigger: document.querySelector("[oraniths-nav-hover]"),
        from: document.querySelector(".nav_content-right"),
        to: document.querySelector(".nav_content-right-2"),
      }
    ]
  };

  if (elements.navButton) {
    document.addEventListener("click", (e) => {
      if (e.target.closest("[nav-close-trigger]")) {
        elements.navButton.click();
      }
    });

    document.addEventListener("keydown", (e) => {
      if (e.key === "Escape") {
        const isVisible = Array.from(elements.closeTriggers).some(
          el => el.offsetParent !== null
        );
        if (isVisible) {
          elements.navButton.click();
        }
      }
    });
  }

  elements.hoverPairs.forEach(({ trigger, from, to }) => {
    if (trigger && from && to) {
      bindOpacityToggle(trigger, from, to);
    }
  });
}

function bindOpacityToggle(trigger, from, to) {
  trigger.addEventListener("mouseenter", () => {
    gsap.set(from, { opacity: 0, overwrite: "auto" });
    gsap.set(to, { opacity: 1, overwrite: "auto" });
  });

  trigger.addEventListener("mouseleave", () => {
    gsap.set(from, { opacity: 1, overwrite: "auto" });
    gsap.set(to, { opacity: 0, overwrite: "auto" });
  });
}

document.addEventListener("DOMContentLoaded", function () {
  if (window.innerWidth <= 768) return;

  const xCoord = document.getElementById("x-coordinate");
  const yCoord = document.getElementById("y-coordinate");

  let mouseX = 0;
  let mouseY = 0;
  let lastX = -1;
  let lastY = -1;
  let ticking = false;

  function formatNumber(num) {
    return num.toString().padStart(4, "0");
  }

  function updateCoordinates() {
    if (mouseX !== lastX || mouseY !== lastY) {
      xCoord.textContent = formatNumber(mouseX % 10000);
      yCoord.textContent = formatNumber(mouseY % 10000);
      lastX = mouseX;
      lastY = mouseY;
    }
    ticking = false;
  }

  document.addEventListener("mousemove", (event) => {
    mouseX = event.clientX;
    mouseY = event.clientY;

    if (!ticking) {
      ticking = true;
      requestAnimationFrame(updateCoordinates);
    }
  });
});

