document.addEventListener("DOMContentLoaded", () => {
  const chars = "ABCDEFGHKLMNOPQRSTUVWXYZ";
  let isClickable = true;
  const indicator = document.getElementById("section-indicator");
  const sections = document.querySelectorAll("[data-title]");
  let currentSection = "";

  function generateRandomChars(count, finalChar) {
    const result = [];
    for (let i = 0; i < count; i++) {
      result.push(chars.charAt(Math.floor(Math.random() * chars.length)));
    }
    result.push(finalChar);
    return result;
  }

  function scrambleAnimate(element, text, useSplitType = false, skipDelay = false) {
    const speed = parseFloat(element.getAttribute("data-speed")) || 8;
    const animationType = element.getAttribute("data-animation") || "sequential";
    const delay = skipDelay ? 0 : parseFloat(element.getAttribute("data-delay")) || 0;

    if (useSplitType) {
      if (element.splitInstance) element.splitInstance.revert();
      element.splitInstance = new SplitType(element, { types: "words, chars" });

      const charsEls = element.querySelectorAll(".char");
      const timeline = gsap.timeline({ paused: true });
      const timeline1 = gsap.timeline();

      charsEls.forEach((char) => {
        const randomChars = generateRandomChars(3, char.textContent);
        char.innerHTML = "";
        randomChars.forEach((c, i) => {
          const span = document.createElement("span");
          span.innerText = c;
          span.style.opacity = "0";
          span.style.position = i < 3 ? "absolute" : "relative";
          if (i < 3) span.style.textShadow = "none";
          if (i === 3) span.classList.add("last");
          char.appendChild(span);
        });
      });

      const items = animationType === "random"
        ? Array.from(charsEls).sort(() => Math.random() - 0.5)
        : charsEls;

      items.forEach((char, index) => {
        timeline1.to(char.children, {
          opacity: 1,
          stagger: {
            each: 0.2,
            onUpdate: function () {
              if (this.progress() > 0.6 && !this._targets[0].classList.contains("last")) {
                this._targets[0].style.opacity = "0";
              }
            }
          }
        }, `index+=${index / 2}`);
      });

      timeline.add(timeline1.timeScale(speed).delay(delay));

      const startPos = element.hasAttribute("data-start-100") ? "top 100%" : "top 85%";
      ScrollTrigger.create({
        trigger: element,
        start: startPos,
        end: "top 50%",
        onEnter: () => timeline.play(),
        onLeaveBack: () => timeline.reverse(),
      });

    } else {
      element.innerHTML = "";
      element.style.whiteSpace = "pre-wrap";

      text.split(" ").forEach((word, wordIndex, arr) => {
        const wordSpan = document.createElement("span");
        wordSpan.style.display = "inline-block";
        word.split("").forEach((char) => {
          const span = document.createElement("span");
          span.style.position = "relative";
          span.style.display = "inline-block";

          const randomChars = generateRandomChars(3, char);
          randomChars.forEach((c, i) => {
            const inner = document.createElement("span");
            inner.innerText = c;
            inner.style.opacity = "0";
            inner.style.position = i < 3 ? "absolute" : "relative";
            if (i < 3) inner.style.textShadow = "none";
            if (i === 3) inner.classList.add("final-char");
            span.appendChild(inner);
          });

          wordSpan.appendChild(span);
        });

        element.appendChild(wordSpan);
        if (wordIndex < arr.length - 1) {
          element.appendChild(document.createTextNode(" "));
        }
      });

      const spans = element.querySelectorAll("span span");
      const items = animationType === "random"
        ? [...spans].sort(() => Math.random() - 0.5)
        : spans;

      const timeline = gsap.timeline().timeScale(speed);
      items.forEach((span, i) => {
        timeline.to(span.children, {
          opacity: 1,
          stagger: {
            each: 0.1,
            onUpdate: function () {
              if (this.progress() > 0.6 && !this._targets[0].classList.contains("final-char")) {
                this._targets[0].style.opacity = "0";
              }
            }
          }
        }, i * 0.05);
      });
    }
  }

  document.querySelectorAll("[data-text]").forEach((el) => {
    scrambleAnimate(el, el.getAttribute("data-text"), true);
  });

  document.querySelectorAll("[data-collection-link]").forEach((link) => {
    link.addEventListener("click", () => {
      if (!isClickable) return;
      isClickable = false;
      setTimeout(() => isClickable = true, 2000);

      const isPrimary = link.getAttribute("data-collection-link") === "1";

      document.querySelectorAll("[data-text]").forEach((el) => {
        const original = el.getAttribute("data-original-text") || el.getAttribute("data-text");
        const newText = el.getAttribute("data-new-text");
        if (!el.hasAttribute("data-original-text")) {
          el.setAttribute("data-original-text", original);
        }
        const target = isPrimary ? original : newText;

        if (target && target !== el.getAttribute("data-text")) {
          gsap.to(el, {
            opacity: 0,
            duration: 0.3,
            onComplete: () => {
              if (el.splitInstance) {
                el.splitInstance.revert();
                el.splitInstance = null;
              }
              el.setAttribute("data-text", target);
              el.innerHTML = target;
              el.splitInstance = new SplitType(el, { types: "words, chars" });
              scrambleAnimate(el, target, true, true);
              gsap.to(el, { opacity: 1, duration: 0.3 });
            }
          });
        }
      });
    });
  });

  const updateIndicator = (newTitle) => {
    if (newTitle !== currentSection) {
      currentSection = newTitle;
      indicator.setAttribute("data-text", newTitle);
      scrambleAnimate(indicator, newTitle, false);
    }
  };

  const setInitialIndicator = () => {
    const rect = sections[0].getBoundingClientRect();
    if (rect.top <= window.innerHeight * 0.6 && rect.bottom >= window.innerHeight * 0.6) {
      const title = sections[0].dataset.title;
      currentSection = title;
      indicator.setAttribute("data-text", title);
      scrambleAnimate(indicator, title, false);
    }
  };

  window.addEventListener("scroll", () => {
    let newSection = "";
    sections.forEach((section) => {
      const rect = section.getBoundingClientRect();
      if (rect.top <= window.innerHeight * 0.6 && rect.bottom >= window.innerHeight * 0.6) {
        newSection = section.dataset.title;
      }
    });
    if (newSection) updateIndicator(newSection);
  });

  setInitialIndicator();

  setTimeout(() => {
    scrambleAnimate(indicator, indicator.getAttribute("data-text"), false);
  }, 100);
});

