document.addEventListener("DOMContentLoaded", () => {
  const preloader = document.querySelector(".preloader-enter");
  const preloaderBtn = document.querySelector(".preloader_button-wrap");
  const lazySections = document.querySelectorAll(".lazy-section");

  const io = new IntersectionObserver(
    (entries, observer) => {
      entries.forEach((entry) => {
        if (entry.isIntersecting) {
          const el = entry.target;
          const label = el.dataset.section || el.id || el.className;
          el.classList.add("active");

          const images = el.querySelectorAll("img[data-src]");
          images.forEach((img) => {
            if (img.dataset.src) {
              img.src = img.dataset.src;
            }
          });

          observer.unobserve(el);
        }
      });
    },
    { rootMargin: "0px 0px 200px 0px" }
  );

  const startObservingSections = () => {
    lazySections.forEach((sec) => io.observe(sec));
  };

  if (!preloader || preloader.style.display === "none") {
    startObservingSections();
    return;
  }

  const preloaderWatcher = new MutationObserver((_, obs) => {
    if (preloader.style.display === "none") {
      startObservingSections();
      obs.disconnect();
    }
  });

  preloaderWatcher.observe(preloader, {
    attributes: true,
    attributeFilter: ["style"],
  });

  if (preloaderBtn) {
    preloaderBtn.addEventListener("click", () => {
      startObservingSections();
      preloaderWatcher.disconnect();
    });
  }
});

