  const lottieElements = document.querySelectorAll("[data-lottie-watch]");

  const observer = new IntersectionObserver(
    (entries) => {
      entries.forEach((entry) => {
        const el = entry.target;
        const instance = lottieLib.getRegisteredAnimations().find(a => a.wrapper === el);
        if (!instance) return;

        if (entry.isIntersecting) {
          instance.play();
        } else {
          instance.pause();
        }
      });
    },
    { threshold: 0.1 }
  );

  lottieElements.forEach((el) => observer.observe(el));
});

